mapper.update:
update bankbook set bookName=#{bookName}, bookContents=#{bookContents},
bookSale=#{bookSale},bookRate=#{bookRate}
where bookNumber=#{bookNumber}

mapper.select:
SELECT * FROM (SELECT ROWNUM R, B.* FROM (SELECT * FROM BANKBOOK WHERE BOOKNUMBER>0 AND
BOOKNAME BOOKCONTENTS
LIKE '%'||#{search}||'%' ORDER BY BOOKNUMBER DESC) B) WHERE R BETWEEN #{startRow} AND #{lastRow}
